// Tri par nom
export const COUNTRIES = COUNTRIES_DATA.sort((a, b) => a.name.localeCompare(b.name, "fr"));

// Fonction pour trouver un pays par son code
export function getCountryByCode(code: string): CountryData | undefined {
  return COUNTRIES.find(c => c.code === code);
}

// Fonction pour trouver un pays par son indicatif
export function getCountryByDialCode(dialCode: string): CountryData | undefined {
  return COUNTRIES.find(c => c.dialCode === dialCode);
}

// Fonction pour trouver le pays d'une ville
export function findCountryByCity(cityName: string): CountryData | undefined {
  if (!cityName) return undefined;
  const normalizedCity = cityName.toLowerCase().trim();
  return COUNTRIES.find(country =>
    country.cities.some(c => c.toLowerCase() === normalizedCity)
  );
}

// Extraire l'indicatif et le numÃ©ro d'un numÃ©ro de tÃ©lÃ©phone
export function parsePhoneNumber(phone: string): { dialCode: string; number: string } {
  if (!phone) return { dialCode: "+212", number: "" };

  // Trier les indicatifs par longueur dÃ©croissante pour matcher les plus longs d'abord
  const sortedCountries = [...COUNTRIES].sort((a, b) => b.dialCode.length - a.dialCode.length);

  for (const country of sortedCountries) {
    if (phone.startsWith(country.dialCode)) {
      return {
        dialCode: country.dialCode,
        number: phone.slice(country.dialCode.length).trim(),
      };
    }
  }

  // Par dÃ©faut, Maroc
  return { dialCode: "+212", number: phone.replace(/^\+/, "") };
}
